@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'CSV File Upload - Interface View'
@Metadata.allowExtensions: true
define root view entity ZETR_I_CSV_UPLOAD
  as select from zetr_t_csvupload as Upload
{
  key Upload.upload_id                                          as UploadId,

      Upload.end_user                                           as EndUser, // ⭐ Semantics kaldırıldı

      Upload.status                                             as Status,

      cast(
        case
          when Upload.filename is initial and Upload.status is initial
            then 'Dosya Yüklenmedi'
          when Upload.filename is not initial and Upload.status is initial
            then 'Dosya Yüklendi'
          when Upload.status = 'C'
            then 'İşlem Tamamlandı'
          when Upload.status = 'E'
            then 'Hata'
          else 'Beklemede'
        end as abap.char(20)
      )                                                         as FileStatus,

      cast(
        case
          when Upload.filename is initial
            then '1'
          when Upload.filename is not initial and Upload.status is initial
            then '2'
          when Upload.status = 'C'
            then '3'
          when Upload.status = 'E'
            then '1'
          else '2'
        end as abap.char(1)
      )                                                         as CriticalityStatus,

      @Semantics.largeObject: {
        mimeType: 'MimeType',
        fileName: 'Filename',
        acceptableMimeTypes: [
          'text/csv',
          'application/vnd.ms-excel',
          'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
        ],
        contentDispositionPreference: #INLINE
      }
      Upload.attachment                                         as Attachment,

      @Semantics.mimeType: true
      Upload.mimetype                                           as MimeType,

      Upload.filename                                           as Filename,

      cast( coalesce( Upload.chunk_size, 10000 ) as abap.int4 ) as ChunkSize,

      @Semantics.user.createdBy: true
      Upload.created_by                                         as CreatedBy,

      @Semantics.systemDateTime.createdAt: true
      Upload.created_at                                         as CreatedAt,

      @Semantics.user.lastChangedBy: true
      Upload.last_changed_by                                    as LastChangedBy,

      @Semantics.systemDateTime.lastChangedAt: true
      Upload.last_changed_at                                    as LastChangedAt
}
